/* login-start */
$(function () {
   $("#login-btn").click(function () {
       var username = $("#username").val();
       var password = $("#password").val();
       if(username!=""&&password!=""){
           var data={
               "username":username,
               "password":password
           };
           $.ajax({
               type: "POST",
               url: "/user/login",
               data: data,
               dataType:"json",
               success: function(r){
                   if(r.status==0){
                       $(location).attr('href',"/index");
                   }
                   else {
                       alert(r.info);
                   }
               },
               error: function(r){
                   alert( "Error");
               }
           });
       }
       else{
           alert("用户名密码不能为空");
       }
   }) ;
});




/******     reg-part1-function    ******/

function successCheck() {
    if (
        $("#inputusername").parent().parent().hasClass("has-success")
        &&$("#inputpassword").parent().parent().hasClass("has-success")
        &&$("#confirmpassword").parent().parent().hasClass("has-success")
        &&$("#inputemail").parent().parent().hasClass("has-success")
    )
    {
        $("#part1now").removeClass("disabled");
        $("#part1next").removeClass("disabled");
    }
}

function setSuccess(e) {
    e.parent().parent().removeClass("has-warning");
    e.parent().parent().removeClass("has-error");
    e.parent().parent().addClass("has-success");
}

function setWarning(e) {
    e.parent().parent().removeClass("has-success");
    e.parent().parent().removeClass("has-error");
    e.parent().parent().addClass("has-warning");
}

function setError(e) {
    e.parent().parent().removeClass("has-warning");
    e.parent().parent().removeClass("has-success");
    e.parent().parent().addClass("has-error");
}


function setDefualt(e) {
    e.parent().parent().removeClass("has-warning");
    e.parent().parent().removeClass("has-success");
    e.parent().parent().removeClass("has-error");
}

$(function () {

    /* usernameinput*/

   $("#inputusername").blur(function () {
       if ($(this).val()!=""){
           var username=$(this).val();
           if (username.length<2){
                setError($(this));
                $("#usernamespan").html("用户名过短");
           }
           else if(username.length>=2){
               setSuccess($(this));
               $("#usernamespan").html("");
               successCheck();
           }
       }
       else if($("#usernamespan").html()!=""){
           setWarning($(this));
           $("#usernamespan").html("");
       }
       else if($("#usernamespan").html()==""){
           setDefualt($(this));
       }
   }) ;
   /* username end */

    /* nicknameinput*/

    $("#inputnickrname").blur(function () {
        if ($(this).val()!=""){
            var username=$(this).val();
                setSuccess($(this));
                $("#nicknamespan").html("");
                successCheck();
        }
        else if($("#nicknamespan").html()!=""){
            setWarning($(this));
            $("#nicknamespan").html("");
        }
        else if($("#nicknamespan").html()==""){
            setDefualt($(this));
        }
    }) ;
    /* nickname end */

    /* passowrdinput */

    $("#inputpassword").blur(function () {
        if($(this).val()==""){
            setDefualt($(this));
            $("#passwordspan").html("字母，数字或者英文符号，最短8位，区分大小写");
        }
        else {
            var passwordstr = /^[a-zA-Z0-9]+$/ ;
            if($("#confirmpassword").val()!=""){
                if(passwordstr.test($(this).val())&&$(this).val().length>8){
                    if($(this).val()==$("#confirmpassword").val()){
                        setSuccess($(this));
                        $("#passwordspan").html("");
                        setSuccess($("#confirmpassword"));
                        $("#cfmpasswordspan").html("");
                        successCheck();
                    }
                    else{
                        setError($(this));
                        $("#passwordspan").html("两次输入密码不一致");
                        setError($("#confirmpassword"));
                        $("#cfmpasswordspan").html("两次输入密码不一致");
                    }
                }
                else{
                    setError($(this));
                    $("#passwordspan").html("密码格式不正确，不可使用大小写字母、数字以外的字符且大于8位");
                }
            }
            else if(!(passwordstr.test($(this).val())&&$(this).val().length>8)){
                setError($(this));
                $("#passwordspan").html("密码只能由字母，数字组成且大于8位");
            }
            else if($("#inputpassword").val()!=""){
                setSuccess($(this));
                $("#passwordspan").html("");
            }
        }
    }); /* password end */

    $("#confirmpassword").blur(function () {
        if($(this).val()==""){
            setDefualt($(this));
            $("#cfmpasswordspan").html("请再次输入密码");
        }
        else {
            var passwordstr = /^[a-zA-Z0-9]+$/ ;
            if($("#inputpassword").val()!=""){
                if(passwordstr.test($(this).val())&&$(this).val().length>8){
                    if($(this).val()==$("#inputpassword").val()){
                        setSuccess($(this));
                        $("#cfmpasswordspan").html("");
                        setSuccess($(this));
                        $("#passwordspan").html("");
                        successCheck();
                    }
                    else{
                        setError($(this));
                        $("#cfmpasswordspan").html("两次输入密码不一致");
                        setError($("#inputpassword"));
                        $("#passwordspan").html("两次输入密码不一致");
                    }
                }
                else{
                    setError($(this));
                    $("#cfmpasswordspan").html("密码只能由字母，数字组成且大于8位");
                }
            }
            else if(!(passwordstr.test($(this).val())&&$(this).val().length>8)){
                setError($(this));
                $("#cfmpasswordspan").html("密码只能由字母，数字组成且大于8位");
            }
            else if($("#confirmpassword").val()!=""){
                setSuccess($(this));
                $("#cfmpasswordspan").html("");
            }
        }
    });  /* password-cofirm end */

    /* email start */

    $("#inputemail").blur(function () {
        if($(this).val()==""){
            setDefualt($(this));
            $("#emailspan").html("");
        }
        else{
            var emailstr=$("#inputemail").val();
            if(!emailstr.match(/^([a-zA-Z0-9_-])+@([a-zA-Z0-9_-])+((\.[a-zA-Z0-9_-]{2,3}){1,2})$/)) {
                setError($(this));
                $("#emailspan").html("邮箱格式不正确");
            }
            else{
                setSuccess($(this));
                $("#emailspan").html("");
                successCheck();
            }
        }
    });/* email end*/

    /****************   reg-now-start  ****************/

    $("#part1now, #part2now").click(function () {
        if($(this).hasClass("disabled")){
            if(!$("#inputusername").parent().parent().hasClass("has-success")){
                $("#inputusername").parent().parent().addClass("has-warning");
            }
            if(!$("#inputpassword").parent().parent().hasClass("has-success")){
                $("#inputpassword").parent().parent().addClass("has-warning");
            }
            if(!$("#confirmpassword").parent().parent().hasClass("has-success")){
                $("#confirmpassword").parent().parent().addClass("has-warning");
            }
            if(!$("#inputemail").parent().parent().hasClass("has-success")){
                $("#inputemail").parent().parent().addClass("has-warning");
            }
            alert("请先完善信息");
        }
        else {
            /* TODO 验证码验证 */
            if(true){
                if($("#check-agree").is(':checked')){
                    //part1
                    var username=$("#inputusername").val();
                    var nickname=$("#inputnickname").val();
                    var password=$("#inputpassword").val();
                    var email=$("#inputemail").val();
                    //part2
                    var realname=$("#inputrealname").val();
                    var gender=$("input[name = 'usersex']:checked").val();
                    var usercode=$("#usercodeinput").val();
                    var phone=$("#phoneinput").val();
                    var schoolid=$('#schoolselect option:selected').val();;
                    var major=$("#inputmajor").val();
                    var userid=$("#inputuserid").val();
                    var data={
                        "username":username,
                        "password":password,
                        "email":email,
                        "nickname":nickname,
                        "realname":realname,
                        "gender":gender,
                        "usercode":usercode,
                        "phone":phone,
                        "schoolid":schoolid,
                        "major":major,
                        "userid":userid
                    };
                    $.ajax({
                        type: "POST",
                        url: "/user/register",
                        data: data,
                        dataType:"json",
                        success: function(r){
                            if(r.status==0){
                                alert("success");
                                $("#reg-nav1").removeClass("reg-nav-on");
                                $("#reg-nav2").removeClass("reg-nav-on");
                                $("#reg-nav3").addClass("reg-nav-on");
                                $(".reg-part1").hide();
                                $(".reg-part2").hide();
                                $(".reg-part3").show();
                                var $height = $(window).height();
                                if($height>$(document.body).height()){
                                    $("footer").css("position","fixed");
                                }
                                $("footer").css("top", $height-53.2);
                            }
                            else {
                                alert(r.info);
                            }
                        },
                        error: function(r){
                            alert( "Error");
                        }
                    });
                }
                else {
                    alert("您需同意我们的协议方可注册");
                }
            }
            else{
                alert("验证码错误");
                $("#codeinput").focus();
            }
        }
    }) ;
    $("#part1next").click(function () {
        if($(this).hasClass("disabled")){
            if(!$("#inputusername").parent().parent().hasClass("has-success")){
                $("#inputusername").parent().parent().addClass("has-warning");
            }
            if(!$("#inputpassword").parent().parent().hasClass("has-success")){
                $("#inputpassword").parent().parent().addClass("has-warning");
            }
            if(!$("#confirmpassword").parent().parent().hasClass("has-success")){
                $("#confirmpassword").parent().parent().addClass("has-warning");
            }
            if(!$("#inputemail").parent().parent().hasClass("has-success")){
                $("#inputemail").parent().parent().addClass("has-warning");
            }
            alert("请先完善信息");
        }
        else {
            /* TODO 验证码验证 */
            if(true){
                if($("#check-agree").is(':checked')){
                    $("#reg-nav1").removeClass("reg-nav-on");
                    $("#reg-nav2").addClass("reg-nav-on");
                    $(".reg-part1").toggle();
                    $(".reg-part2").toggle();
                }
                else {
                    alert("您需同意我们的协议方可注册");
                }
            }
            else {
                alert("验证码错误");
                $("#codeinput").focus();
            }
        }
    });
    /****************   reg-now-end  ****************/

});/* reg-part1-function end */


