/******     reg-part2-function    ******/
$(function(){

    /* realname start */
    $("#inputrealname").blur(function () {
        if($(this).val()==""){
            setDefualt($(this));
            $("#realnamespan").html("");
        }
        else{
            var realnamestr=$("#inputrealname").val();
            if(!realnamestr.match(/^(?!\s+$)[\u4E00-\u9FA0]+$|^[a-z\s·]+$/i)) {
                setError($(this));
                $("#realnamespan").html("真实姓名不正确");
            }
            else{
                setSuccess($(this));
                $("#realnamespan").html("");
                successCheck();
            }
        }
    });
    /* realname end */

    /* usercode start */
    $("#usercodeinput").blur(function () {
        if($(this).val()==""){
            setDefualt($(this));
            $("#usercodespan").html("");
        }
        else{
            var usercodestr=$("#usercodeinput").val();
            if(!usercodestr.match(/^[1-9]\d{5}[1-9]\d{3}((0\d)|(1[0-2]))(([0|1|2]\d)|3[0-1])\d{3}([0-9]|X)$/)&&!usercodestr.match(/^[1-9]\d{7}((0\d)|(1[0-2]))(([0|1|2]\d)|3[0-1])\d{3}$/)) {
                setError($(this));
                $("#usercodespan").html("身份证号码不合规范");
            }
            else{
                setSuccess($(this));
                $("#usercodespan").html("");
                successCheck();
            }
        }
    });

    /* usercode end */

    /* phoneinput start */
    $("#phoneinput").blur(function () {
        if($(this).val()==""){
            setDefualt($(this));
            $("#phonespan").html("");
        }
        else{
            var phonestr=$("#phoneinput").val();
            if(!phonestr.match(/^1[0-9]{10}$/)) {
                setError($(this));
                $("#phonespan").html("手机号码不正确");
            }
            else{
                setSuccess($(this));
                $("#phonespan").html("");
                successCheck();
            }
        }
    });
    /* phoneinput end */

    /* userid start */
    $("#inputuserid").blur(function () {
        if($(this).val()==""){
            setDefualt($(this));
            $("#useridspan").html("");
        }
        else{
            var useridstr=$("#inputuserid").val();
            if(!useridstr.match(/^[0-9]*$/)) {
                setError($(this));
                $("#useridspan").html("id不正确");
            }
            else{
                setSuccess($(this));
                $("#useridspan").html("");
                successCheck();
            }
        }
    });

    $("#part2re").click(function () {
        $("#reg-nav2").removeClass("reg-nav-on");
        $("#reg-nav1").addClass("reg-nav-on");
        $(".reg-part1").toggle();
        $(".reg-part2").toggle();
    });
    /* userid start */
});
/* reg-part2-function end */